import React from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import { Link }  from 'react-router-dom';
import propType from 'prop-types';
import { GridList, GridTile} from 'material-ui/GridList';
import IconButton from 'material-ui/IconButton';
import ZoomIn from 'material-ui/svg-icons/action/zoom-in';
import Dialog from 'material-ui/Dialog';
import FlatButton from 'material-ui/FlatButton';
import FilmDetails from '../film-details/FilmDetails';

class ImageResults extends React.Component {
    state = {
        open: false,
        seleted: false,
        film:{},
        currentImg: ""
    }

    handleOpen = img => {
        //this.setState({ open: true, currentImg: img });
        this.setState({ seleted: true, film: img });
    }

    handleClose = () => {
        this.setState({ open: false});
    }

    render() {
        let imageListContent;
        const {images} = this.props;
        const {kk} = this.props;
    //    kk = 4;
        {console.log(kk)};

        if (images) {
            imageListContent = (
                <GridList cols={6}>
                    {images.map(img => (
                        <GridTile
                            title = {img.show.name}
                            key = {img.show.id}
                            subtitle={
                                <span>
                                    type <strong>{img.show.type}</strong>
                                </span>
                            }
                            actionIcon = {
                            <IconButton onClick={() => this.handleOpen(img)} >
                                    <ZoomIn color="white" />
                                </IconButton>
                            }
                            >
                        <img src={(img.show.image && img.show.image.medium)? img.show.image.medium : 'https://static.addtoany.com/images/dracaena-cinnabari.jpg'} alt="" />
                        </GridTile>
                    ))}
                </GridList>
            )
            //<img src={img.largeImageURL} alt="" />
        } else {
            console.log('heehehehehehehehheh')
            imageListContent = null;
        }

        const action = [
            <FlatButton label="Close" primary={true} onClick={this.handleClose} />
        ];
            return (
                <div>
                    {!this.state.seleted}? {imageListContent}  <FilmDetails selectedFilm={this.state.film} />
                    <Dialog
                        action = {action}
                        modal = {false}
                        open = {this.state.open}
                        onRequestClose= {this.handleClose} >
                        <img src={this.state.currentImg} alt="" style={{ width: '100%'}} />
                    </Dialog>
                </div>
            )
    }
}

ImageResults.propType = {
    images: propType.array.isRequired
}

export default ImageResults;
